namespace Mageplaza\ReviewReminder\Setup;

use Magento\Framework\Setup\ModuleContextInterface;
use Magento\Framework\Setup\SchemaSetupInterface;
use Magento\Framework\Setup\UpgradeSchemaInterface;

/**
 * Class UpgradeSchema
 * @package Mageplaza\ReviewReminder\Setup
 */
class UpgradeSchema implements UpgradeSchemaInterface
{
    /**
     * {@inheritdoc}
     */
    public function upgrade(SchemaSetupInterface $setup, ModuleContextInterface $context)
    {
        $installer = $setup;
        $installer->startSetup();

        if (version_compare($context->getVersion(), '1.0.1', '<')
            && $installer->tableExists('mageplaza_reviewbooster_logs')) {
            $installer->getConnection()
                ->renameTable(
                    $installer->getTable('mageplaza_reviewbooster_logs'),
                    $installer->getTable('mageplaza_reviewreminder_logs')
                );
        }

        $installer->endSetup();
    }
}
